import React from 'react';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom'; // Link 추가

// id를 Props로 받도록 수정
const MenuItem = ({ id, name, price, description, image }) => { 
    const detailPath = `/menu/${id}`; 

    return (
        <div className="col-md-4 mb-4">
            {/* Link로 Card 전체를 감쌉니다. 카드 전체를 클릭 가능하게 합니다. */}
            <Link to={detailPath} style={{ textDecoration: 'none', color: 'inherit' }}>
                <Card className="h-100 shadow-sm border-0">
                    <Card.Img variant="top" src={image} style={{ height: '200px', objectFit: 'cover' }} />
                    <Card.Body className="d-flex flex-column">
                        <Card.Title className="fw-bold">{name}</Card.Title>
                        <Card.Text className="text-muted">
                            {description}
                        </Card.Text>
                        
                        {/* 주문하기 버튼은 링크에서 제외하고 별도로 렌더링 */}
                        <div className="mt-auto d-flex justify-content-between align-items-center">
                            <span className="fs-5 fw-bold text-dark">{price.toLocaleString()}원</span>
                            
                            {/* 3. 주문하기 버튼은 Link 안에 포함하지 않고 Card.Body 내부에 유지하거나, 
                                <Link to={detailPath}> 외부에서 렌더링해야 합니다. 
                                여기에 있는 Button은 '상세페이지' 이동이 아닌 '주문' 기능을 가정하고, 
                                 Card를 클릭했을 때만 상세 페이지로 이동하도록 설정하는 것이 일반적입니다. */}
                            <Button variant="outline-dark" size="sm" onClick={(e) => { e.preventDefault(); /* 주문 로직 */ }}>주문하기</Button>
                        </div>
                    </Card.Body>
                </Card>
            </Link>
        </div>
    );
};

export default MenuItem;
