import React, { useState } from 'react';
import menuData from '../db/menuData';
import MenuItem from './MenuItem';
import { Container, Nav } from 'react-bootstrap';

const MenuPage = ({ menuSort = "" }) => {
    // State to track the selected category
    const [selectedCategory, setSelectedCategory] = useState("전체 메뉴");

    // Filter logic: 
    // If "전체 메뉴" is selected, show all categories (menuData).
    // Otherwise, filter menuData to only include the category that matches selectedCategory.
    const filteredData = selectedCategory === "전체 메뉴"
        ? menuData
        : menuData.filter(section => section.category === selectedCategory);

    return (
        <div className="menu-page py-5">
            <Container>
                {/* Category Nav */}
                {/* Use a flex container for the nav to handling many items if needed */}
                <Nav className="justify-content-center mb-5 bg-white py-3 " style={{ zIndex: 100 }}>
                    {menuData.map((section) => (
                        <Nav.Item key={section.id}>
                            <Nav.Link
                                onClick={() => setSelectedCategory(section.category)}
                                className={`fw-bold px-3 ${selectedCategory === section.category ? 'active' : 'text-dark'}`}
                                style={{
                                    cursor: 'pointer',
                                    fontSize: '1.1rem',
                                    color: selectedCategory === section.category ? '#f5426c' : undefined,
                                    borderBottom: selectedCategory === section.category ? '2px solid #f5426c' : undefined
                                }}
                            >
                                {section.category}
                            </Nav.Link>
                        </Nav.Item>
                    ))}
                </Nav>

                {/* Display Filtered Sections */}
                {filteredData.map((section) => {
                    // Skip sections with no items (e.g., "전체 메뉴" placeholder if it has no items)
                    if (!section.items || section.items.length === 0) return null;

                    // 정렬 적용: menuSort 값에 따라 섹션 내 items 복사본을 정렬
                    const itemsToRender = section.items ? [...section.items] : [];
                    if (menuSort === 'low') {
                        itemsToRender.sort((a, b) => a.price - b.price);
                    } else if (menuSort === 'high') {
                        itemsToRender.sort((a, b) => b.price - a.price);
                    } else if (menuSort === 'name') {
                        itemsToRender.sort((a, b) => (a.name || '').localeCompare(b.name || ''));
                    }

                    return (
                        <div id={`category-${section.id}`} key={section.id} className="mb-5 category-section">
                            <h3 className="border-bottom pb-2 mb-4 border-dark">{section.category}</h3>
                            <div className="row">
                                {itemsToRender.map((item) => (
                                    <MenuItem key={item.id} id={item.id} {...item} />
                                ))}
                            </div>
                        </div>
                    );
                })}
            </Container>
        </div>
    );
};

export default MenuPage;
