import React from 'react';
import { useParams } from "react-router-dom";
import { Nav } from 'react-bootstrap'
import { useState, useEffect} from 'react';
import { addItem } from '../store.js'
import { useDispatch } from 'react-redux'
import { Button } from 'react-bootstrap'
import { Link } from 'react-router-dom';
import menuData from '../db/menuData';



function Detail(props) {

    let [tap, setTap] = useState(0);   
    const {paramId} = useParams();
    //console.log(paramId);
    //const { imgUrl, title, content, price } = props.fruit[paramId];

    // dispatch 정의 추가
    const dispatch = useDispatch(); 

     // 상품 유효성 체크 (이건 Hook 호출 이후에 실행되어야 함)
    let selproduct = null;
    menuData.forEach(section => {
        if (section.items) {
            const item = section.items.find(item => item.id === Number(paramId));
            if (item) selproduct = item;
        }
    });

     // 훅은 조건문(if) 아래에서 호출하면 안 됨 (React가 Hook 순서 기억을 못함)
    if (!selproduct) {
        return <div>해당 상품이 존재하지 않습니다.</div>;
    }

    const { id, name, price, description, image } = selproduct;

    // console.log("내가 선택한 상품은: " + id + " " + title);

    

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-6">
                    
                    {imgUrl ? (
                        <img src={process.env.PUBLIC_URL +"/"+ imgUrl} width="100%" alt={title || name} />
                    ) : (
                        <img src={image} width="100%" alt={title || name} />
                    )}
                
                </div>
                <div className="col-md-6">
                    <h5 className="pt-5" style={{ fontSize: '2rem' }}>{title || name}</h5>
                    <p style={{ fontSize: '1.2rem' }}>{content}</p>
                    <p style={{ fontSize: '1.5rem', fontWeight: 'bold', padding: 10 }}>{price.toLocaleString()}원</p>
                    
                    <button className="btn btn-danger"
                    variant="primary"
                        onClick={() => {
                        const imgFile = imgUrl 
                            ? imgUrl.replace("img/", "")
                            : image.replace(process.env.PUBLIC_URL + '/img/', '');

                        dispatch(
                            addItem({
                            id: id,
                            imgurl: imgFile,
                            name: title || name,
                            count: 1,
                            price: price
                            })
                        );
                        }}
                        style={{ marginRight: "10px", fontSize: '1.2rem' }}
                    >주문하기</button>
                    <Link to="/cart" >
                        <Button variant="success" style={{ fontSize: '1.2rem' }}>  주문상품 확인하기 </Button>
                    </Link>
                </div>
            </div>

            <TabContent tap={tap}/>

        </div>
    )
}

function TabContent({tap}){
    let [fade, setFade] = useState('')
    useEffect( ()=>{
        setTimeout(()=>{ setFade('end')},10)
        return ()=>{
            setFade('')
        }
    } ,[tap])

    return null;
}

export default Detail;
