import React, { useState } from 'react';
import menuData from '../db/menuData';
import MenuItem from './MenuItem';
import { Container, Nav } from 'react-bootstrap';

const MenuPage = () => {
    // State to track the selected category
    const [selectedCategory, setSelectedCategory] = useState("전체 메뉴");

    // Filter logic: 
    // If "전체 메뉴" is selected, show all categories (menuData).
    // Otherwise, filter menuData to only include the category that matches selectedCategory.
    const filteredData = selectedCategory === "전체 메뉴"
        ? menuData
        : menuData.filter(section => section.category === selectedCategory);

    return (
        <div className="menu-page py-5">
            <Container>
                {/* Category Nav */}
                {/* Use a flex container for the nav to handling many items if needed */}
                <Nav className="justify-content-center mb-5 sticky-top bg-white py-3 shadow-sm border-bottom" style={{ zIndex: 100 }}>
                    {menuData.map((section) => (
                        <Nav.Item key={section.id}>
                            <Nav.Link
                                onClick={() => setSelectedCategory(section.category)}
                                className={`fw-bold px-3 ${selectedCategory === section.category ? 'text-primary active border-bottom border-primary' : 'text-dark'}`}
                                style={{ cursor: 'pointer', fontSize: '1.1rem' }}
                            >
                                {section.category}
                            </Nav.Link>
                        </Nav.Item>
                    ))}
                </Nav>

                {/* Display Filtered Sections */}
                {filteredData.map((section) => (
                    <div id={`category-${section.id}`} key={section.id} className="mb-5 category-section">
                        <h3 className="border-bottom pb-2 mb-4 border-dark">{section.category}</h3>
                        <div className="row">
                            {section.items.map((item) => (
                                <MenuItem key={item.id} {...item} />
                            ))}
                        </div>
                    </div>
                ))}
            </Container>
        </div>
    );
};

export default MenuPage;
