import React from 'react';
import { useParams, useNavigate } from "react-router-dom";
import { Nav } from 'react-bootstrap'
import { useState, useEffect} from 'react';
import { addItem } from '../store.js'
import { useDispatch } from 'react-redux'
import { Button } from 'react-bootstrap'
import { Link } from 'react-router-dom';
import menuData from '../db/menuData';




function Detail(props) {


    let [tap, setTap] = useState(0);   
    const {paramId} = useParams();
    const navigate = useNavigate();  // ÌéòÏù¥ÏßÄ Ïù¥ÎèôÏùÑ ÏúÑÌïú ÌõÖ
    
    //console.log(paramId);
    //const { imgUrl, title, content, price } = props.fruit[paramId];


    // dispatch Ï†ïÏùò Ï∂îÍ∞Ä
    const dispatch = useDispatch(); 


     // ÏÉÅÌíà Ïú†Ìö®ÏÑ± Ï≤¥ÌÅ¨ (Ïù¥Í±¥ Hook Ìò∏Ï∂ú Ïù¥ÌõÑÏóê Ïã§ÌñâÎêòÏñ¥Ïïº Ìï®)
    let selproduct = null;
    menuData.forEach(section => {
        if (section.items) {
            const item = section.items.find(item => item.id === Number(paramId));
            if (item) selproduct = item;
        }
    });


     // ÌõÖÏùÄ Ï°∞Í±¥Î¨∏(if) ÏïÑÎûòÏóêÏÑú Ìò∏Ï∂úÌïòÎ©¥ Ïïà Îê® (ReactÍ∞Ä Hook ÏàúÏÑú Í∏∞ÏñµÏùÑ Î™ªÌï®)
    if (!selproduct) {
        return <div>Ìï¥Îãπ ÏÉÅÌíàÏù¥ Ï°¥Ïû¨ÌïòÏßÄ ÏïäÏäµÎãàÎã§.</div>;
    }


    const { id, name, price, description, image } = selproduct;

    // Î™®Îì† ÏÉÅÌíà IDÎ•º ÏàúÏÑúÎåÄÎ°ú Î∞∞Ïó¥Ïóê Î™®ÏúºÍ∏∞
    const allProductIds = [];
    menuData.forEach(section => {
        if (section.items) {
            section.items.forEach(item => {
                allProductIds.push(item.id);
            });
        }
    });

    // ÌòÑÏû¨ ÏÉÅÌíàÏùò Ïù∏Îç±Ïä§ Ï∞æÍ∏∞
    const currentIndex = allProductIds.indexOf(Number(paramId));

    // Ïù¥Ï†Ñ ÏÉÅÌíàÏúºÎ°ú Ïù¥Îèô
    const handlePrevClick = () => {
        if (currentIndex > 0) {
            const prevId = allProductIds[currentIndex - 1];
            navigate(`/detail/${prevId}`);
        }
    };

    // Îã§Ïùå ÏÉÅÌíàÏúºÎ°ú Ïù¥Îèô
    const handleNextClick = () => {
        if (currentIndex < allProductIds.length - 1) {
            const nextId = allProductIds[currentIndex + 1];
            navigate(`/detail/${nextId}`);
        }
    };

    // console.log("ÎÇ¥Í∞Ä ÏÑ†ÌÉùÌïú ÏÉÅÌíàÏùÄ: " + id + " " + title);


    


    return (
        <div className="container">
            <div className="row">
                <div className="col-md-6">
                    
                    <img src={image} width="100%" alt={name} />
                
                </div>
                
                {/* üî• ÌôîÏÇ¥Ìëú Î≤ÑÌäº Ï∂îÍ∞Ä */}
            <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', marginTop: '40px', marginBottom: '40px' }}>
                <button 
                    onClick={handlePrevClick}
                    disabled={currentIndex === 0}
                    style={{
                        padding: '10px 20px',
                        fontSize: '1.2rem',
                        cursor: currentIndex === 0 ? 'not-allowed' : 'pointer',
                        opacity: currentIndex === 0 ? 0.5 : 1,
                        backgroundColor: '#6c757d',
                        color: 'white',
                        border: 'none',
                        borderRadius: '5px'
                    }}
                >
                    ‚Üê Ïù¥Ï†Ñ Î©îÎâ¥
                </button>

                <button 
                    onClick={handleNextClick}
                    disabled={currentIndex === allProductIds.length - 1}
                    style={{
                        padding: '10px 20px',
                        fontSize: '1.2rem',
                        cursor: currentIndex === allProductIds.length - 1 ? 'not-allowed' : 'pointer',
                        opacity: currentIndex === allProductIds.length - 1 ? 0.5 : 1,
                        backgroundColor: '#6c757d',
                        color: 'white',
                        border: 'none',
                        borderRadius: '5px'
                    }}
                >
                    Îã§Ïùå Î©îÎâ¥ ‚Üí
                </button>
            </div>

                <div className="col-md-6">
                    <h5 className="pt-5" style={{ fontSize: '2rem', padding: 30}}>{name}</h5>
                    <p style={{ fontSize: '1.1rem', fontWeight: 'light', color: '#504c4cff'}}>{description}</p>
                    <p style={{ fontSize: '1.5rem', fontWeight: 'bold', padding: 10 }}>{price.toLocaleString()}Ïõê</p>
                    
                    <button className="btn btn-danger"
                    variant="primary"
                        onClick={() => {
                        const imgFile = image.replace(process.env.PUBLIC_URL + '/img/', '');


                        dispatch(
                            addItem({
                            id: id,
                            imgurl: imgFile,
                            name: name,
                            count: 1,
                            price: price
                            })
                        );
                        }}
                        style={{ marginRight: "10px", fontSize: '1.2rem' }}
                    >Ï£ºÎ¨∏ÌïòÍ∏∞</button>
                    <Link to="/cart" >
                        <Button variant="success" style={{ fontSize: '1.2rem' }}>  Ï£ºÎ¨∏ÏÉÅÌíà ÌôïÏù∏ÌïòÍ∏∞ </Button>
                    </Link>
                </div>
            </div>

            

            <TabContent tap={tap}/>


        </div>
    )
}


function TabContent({tap}){
    let [fade, setFade] = useState('')
    useEffect( ()=>{
        setTimeout(()=>{ setFade('end')},10)
        return ()=>{
            setFade('')
        }
    } ,[tap])


    return null;
}


export default Detail;
