const Title = () => {
    return (
        <div style={{
            width: '100%',
            height: '700px', // 원하는 높이로 조정
            backgroundImage: `url(${process.env.PUBLIC_URL + '/img/int_img4.jpg'})`, // 배경 이미지 경로 수정
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundAttachment: 'fixed', // 스크롤 시 배경 고정
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            position: 'relative'
        }}>
            <h1 style={{
                color: '#ffffff', // 텍스트 색상 (배경에 맞게 조정)
                fontSize: '48px',
                textAlign: 'center',
                lineHeight: 1.5,
                letterSpacing: '0.05em',
                textShadow: '0 2px 10px rgba(0, 0, 0, 0.5)' // 가독성을 위한 그림자
            }}>
                제대로 된 셰프의 <br /> 제대로 만든 초밥을 즐기다.
            </h1>
        </div>
    );
};

export default Title;
