import React from 'react';
import menuData from '../db/menuData';
import MenuItem from './MenuItem';
import { Container, Nav } from 'react-bootstrap';

const MenuPage = () => {
    // Optional: state for active category if we want filtering, 
    // but the reference site usually lists them all. Let's do a scroll-to or list all style.
    // For a cleaner UI, let's list all but allow jumping or filtering.
    // Let's stick to listing all by category for now, matching the reference's long scroll.

    return (
        <div className="menu-page py-5">
            <Container>
                <div className="text-center mb-5">
                    <h2 className="display-4 fw-bold">MENU</h2>
                    <p className="lead text-secondary">상무초밥의 정성스러운 메뉴를 소개합니다.</p>
                </div>

                {/* Optional Category Nav (Scroll Spy style can be added later) */}
                <Nav className="justify-content-center mb-5 sticky-top bg-white py-3 shadow-sm" style={{ zIndex: 100 }}>
                    {menuData.map((section) => (
                        <Nav.Item key={section.id}>
                            <Nav.Link href={`#category-${section.id}`} className="text-dark fw-bold px-3">
                                {section.category}
                            </Nav.Link>
                        </Nav.Item>
                    ))}
                </Nav>

                {menuData.map((section) => (
                    <div id={`category-${section.id}`} key={section.id} className="mb-5 category-section">
                        <h3 className="border-bottom pb-2 mb-4 border-dark">{section.category}</h3>
                        <div className="row">
                            {section.items.map((item) => (
                                <MenuItem key={item.id} {...item} />
                            ))}
                        </div>
                    </div>
                ))}
            </Container>
        </div>
    );
};

export default MenuPage;
