/* eslint-disable */
import { useState, useEffect } from 'react';
import './App.css';
import { Navbar, Container, Nav } from 'react-bootstrap'
import { Routes, Route, useNavigate } from 'react-router-dom'
import Detail from './components/Detail';
import NotFound from './components/NotFound';
import Member from './components/Member';
import Location from './components/Location';
import About from './components/About';
import Title from './components/Title';
import Footer from './components/Footer';
import Cart from "./components/Cart";
import Board from "./components/Board";
import MenuPage from "./components/MenuPage";
import menuData from "./db/menuData";
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, EffectFade } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/effect-fade';
import { Link } from 'react-router-dom';


function App() {

  const allMenuItems = menuData.flatMap(section => section.items || []);
  const [menu, setMenu] = useState(allMenuItems);
  const [menuSort, setMenuSort] = useState("");
  const navigate = useNavigate();


  const sortByName = () => {
    let sortedMenu = [...menu].sort((a, b) => a.name.localeCompare(b.name, 'ko-KR'));
    setMenu(sortedMenu);
  };


  const sortByPriceLowToHigh = () => {
    let sortedMenu = [...menu].sort((a, b) => a.price - b.price);
    setMenu(sortedMenu);
  };


  const sortByPriceHighToLow = () => {
    let sortedMenu = [...menu].sort((a, b) => b.price - a.price);
    setMenu(sortedMenu);
  };


  const sliderImages = [
    process.env.PUBLIC_URL + '/img/slider_image/1.jpg',
    process.env.PUBLIC_URL + '/img/slider_image/2.jpg',
    process.env.PUBLIC_URL + '/img/slider_image/3.jpg',
    process.env.PUBLIC_URL + '/img/slider_image/4.jpg',
    process.env.PUBLIC_URL + '/img/slider_image/5.jpg',
    process.env.PUBLIC_URL + '/img/slider_image/6.jpg',
    process.env.PUBLIC_URL + '/img/slider_image/7.jpg',
  ];


  const [currentImageIndex, setCurrentImageIndex] = useState(0);


  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % sliderImages.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [sliderImages.length]);


  const handleSortChange = (val) => {
    //console.log('App.js: 정렬 기준 변경 확인 ->', val);
    setMenuSort(val);
    if (val === "low") sortByPriceLowToHigh();
    if (val === "high") sortByPriceHighToLow();
    if (val === "name") sortByName();
  };

  return (
    <div className="App">
      <img src={process.env.PUBLIC_URL + "/img/header.png"} alt="헤더 이미지" style={{ width: '30%', margin: 50 }} />
      <Navbar>
        <Container>
          <Nav className="mx-auto justify-content-center menu gap-5 ">
            <Nav.Link onClick={() => { navigate('/') }}>홈으로</Nav.Link>
            <Nav.Link onClick={() => { navigate('/menu') }}>메뉴</Nav.Link>
            <Nav.Link onClick={() => { navigate('/detail/201') }}>상세페이지</Nav.Link>
            <Nav.Link onClick={() => { navigate('/cart') }}>장바구니</Nav.Link>
            <Nav.Link onClick={() => { navigate('/about') }}>회사소개</Nav.Link>
            <Nav.Link onClick={() => { navigate("/Board"); }}> 게시판</Nav.Link>
          </Nav>
        </Container>
      </Navbar>
      <Routes>
        <Route path="/" element={
          <div>

            <Swiper
              modules={[Autoplay, EffectFade]}
              effect="fade"
              fadeEffect={{ crossFade: true }} 
              autoplay={{ 
                delay: 3000, 
                disableOnInteraction: false 
              }}
              loop={true}
              speed={2000} 
              style={{ height: "600px", width: "100%" }}
            >
              {sliderImages.map((img, index) => (
                <SwiperSlide key={index}>
                  <div 
                    className="slider"
                    style={{
                      backgroundImage: `url(${img})`,
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center'
                    }}
                  >
                    <div className="slider-text-overlay">
                      <h1>제대로 만든 초밥</h1>
                      <h1>가까이 하다</h1>
                      <p style={{ letterSpacing: '-0.01rem' }}>제대로 된 초밥의 대중화를 선도하다.</p>
                    </div>
                  </div>
                </SwiperSlide>
              ))}
            </Swiper>

          
            {/* 메뉴 박스들 */}
            <div style={{marginTop : '40px'}}>
              <h2>상무초밥 메뉴 </h2>
              <p style={{color : '#000000',  letterSpacing : '1.2px', fontSize : '18px', width : '40%', margin : '0 auto' , 
                marginTop : '20px', marginBottom : '20px'}}>
                  상무초밥만의 특별하고 다양한 메뉴
              </p>

              <div style={{display : 'flex', justifyContent : 'center', gap : '50px', marginBottom : '50px'}}>
                <div >
                  <Link to="/menu?category=전체 메뉴">
                    <div className='menubox1' 
                    style={{width : 230, height : 450, position : 'relative', 
                    backgroundImage: `url(${process.env.PUBLIC_URL + '/img/imgi_154_1-1.jpg'})`, 
                    backgroundSize: 'cover', backgroundPosition: 'center', borderRadius : '10px', 
                    boxShadow : '0 3px 8px rgba(0, 0, 0, 0.3)', textAlign : 'center',}}>

                      <span style={{color : '#ffffff',  position : 'absolute', bottom: '10px', 
                        left: '50%', transform: 'translateX(-50%)', fontSize: '20px', 
                        textShadow: '0 0 5px rgba(0, 0, 0, 0.5)'}}>
                          초밥
                      </span>
                    
                    </div>
                  </Link>
                </div>
                <div>
                  <Link to="/menu?category=사시미">
                    <div className='menubox2' 
                    style={{width : 230, height : 450, position : 'relative', 
                    backgroundImage: `url(${process.env.PUBLIC_URL + '/img/imgi_157_packing_img2.jpg'})`, 
                    backgroundSize: 'cover', backgroundPosition: 'center', borderRadius : '10px', 
                    boxShadow : '0 3px 8px rgba(0, 0, 0, 0.3)', textAlign : 'center',}}>

                      <span style={{color : '#ffffff',  position : 'absolute', bottom: '10px', 
                        left: '50%', transform: 'translateX(-50%)', fontSize: '20px', 
                        textShadow: '0 0 5px rgba(0, 0, 0, 0.5)'}}>
                          사시미
                      </span>
                    
                    </div>
                  </Link>
                </div>
                <div>
                  <Link to="/menu?category=사이드">
                    <div className='menubox3' 
                    style={{width : 230, height : 450, position : 'relative', 
                    backgroundImage: `url(${process.env.PUBLIC_URL + '/img/side_menu/601.jpg'})`, 
                    backgroundSize: 'cover', backgroundPosition: 'center', borderRadius : '10px', 
                    boxShadow : '0 3px 8px rgba(0, 0, 0, 0.3)', textAlign : 'center',}}>

                      <span style={{color : '#ffffff',  position : 'absolute', bottom: '10px', 
                        left: '50%', transform: 'translateX(-50%)', fontSize: '20px', 
                        textShadow: '0 0 5px rgba(0, 0, 0, 0.5)'}}>
                          사이드
                      </span>
                    
                    </div>
                  </Link>
                </div>
              </div>
            </div>  
             {/* 메뉴 박스들 end */}

            <div style={{
                width: '100%',
                height: '1000px',
                backgroundImage: `url(${process.env.PUBLIC_URL + '/img/imgi_43_lyr4_bg.jpg'})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                position: 'relative'
              }}>
                
              <img 
                src={process.env.PUBLIC_URL + '/img/lyr4_ttl.png'} 
                alt='5가지 약속 타이틀' 
                style={{marginTop : 100}} />

              <div>
                <p style={{color : '#ffffff', fontSize : '24px', lineHeight : '2.2', width : '100%', margin : '0 auto' , marginTop : '80px'}}>
                  
                  건강하고 안전한 식재료만 쓰겠습니다.<br/>
                  
                  최상급 쌀로만 밥을 짓겠습니다.<br/>
                  
                  음식에 가격 이상의 가치를 담아내겠습니다.<br/>
                  
                  대한민국 초밥 대중화를 선도하겠습니다.<br/>
                  
                  전국 최고의 초밥 브랜드가 되기 위해 목숨 걸고 노력하겠습니다.
                
                </p>
              </div>

              <img 
                src={process.env.PUBLIC_URL + '/img/lyr4_logo.png'} 
                alt='5가지 약속 로고' 
                style={{ position: 'absolute', bottom: '150px', left: '48%', height: 'auto', width: 'auto',}} />
            </div>

            <Title />

            <div style={{
              width: '100%',
              height: '1000px',
              backgroundImage: `url(${process.env.PUBLIC_URL + '/img/imgi_44_lyr5_bg.jpg'})`,
              backgroundSize: 'cover',
              backgroundPosition: 'center',
              position: 'relative'
            }}></div>

            <div className="section"
              style={{
                width: '100%',
                height: '1000px',
                backgroundImage: `url(${process.env.PUBLIC_URL + '/img/section.jpg'})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                position: 'relative'
              }}>
              <img src={`${process.env.PUBLIC_URL}/img/vertical.png`} alt="vertical" style={{
                position: 'absolute',
                left: '160px',
                top: '50%',
                transform: 'translateY(-50%)',
                height: 'auto',
                width: 'auto',
                maxHeight: '80%'
              }} />

              <div className="section-text-overlay" style={{
                position: 'absolute',
                top: '75%',
                left: '150px',
                transform: 'translateY(-50%)',
                color: '#000000ff',
                padding: '8px 10px',
                borderRadius: '6px',
                textAlign: 'center',
                fontWeight: 600,
                letterSpacing: '0.04em'
              }}>
                <div style={{ fontSize: '1.2rem', lineHeight: 1.4, textAlign: 'left', fontWeight: '300' }}>
                  올곧은 재료가 올곧은 품질을 만듭니다.
                  <br />
                  믿고 먹을 수 있는 신선한 식재료를 최우선합니다
                </div>
              </div>
            </div>

            <Footer />
          </div>} />

        <Route path="detail/:paramId" element={<Detail />} />
        <Route path="/cart" element={<Cart />} />

        <Route path="about" element={<About />} >
          <Route path="member" element={<Member />} />
          <Route path="location" element={<Location />} />
        </Route>

        <Route path="/board" element={<Board />} />
        <Route path="/menu" element={<MenuPage menu={menu} menuSort={menuSort} onSortChange={handleSortChange} />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
    </div>
  );
}

export default App;
